import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Polyline, Marker, Popup, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./App.css";

// Custom 3D Pin Markers
const busIcon = L.divIcon({
  html: `
    <div class="bus-pin-marker">
      <div class="bus-pin"></div>
      <div class="bus-icon-pin">üöå</div>
      <div class="bus-pin-shadow"></div>
    </div>
  `,
  iconSize: [40, 50],
  iconAnchor: [20, 50],
  popupAnchor: [0, -50],
  className: 'custom-bus-icon'
});

const userIcon = L.divIcon({
  html: `
    <div class="user-pin-marker">
      <div class="user-pin"></div>
      <div class="user-icon-pin">üìç</div>
      <div class="user-pin-shadow"></div>
    </div>
  `,
  iconSize: [30, 40],
  iconAnchor: [15, 40],
  popupAnchor: [0, -40],
  className: 'custom-user-icon'
});

// Route: Dehradun ‚Üí Delhi
const routeCoords = [
  [30.3165, 78.0322], // Dehradun
  [29.8661, 77.8945], // Roorkee
  [29.4727, 77.7085], // Muzaffarnagar
  [29.2100, 77.0400], // Meerut
  [28.6139, 77.2090], // Delhi
];

function MoveMapToBus({ busPosition }) {
  const map = useMap();
  useEffect(() => {
    if (busPosition) map.panTo(busPosition);
  }, [busPosition, map]);
  return null;
}

function App() {
  const [busIndex, setBusIndex] = useState(0);
  const [busPos, setBusPos] = useState(routeCoords[0]);
  const [userPos, setUserPos] = useState(null);
  const [distance, setDistance] = useState(null);
  const [status, setStatus] = useState("Ready to Track");
  const [isTracking, setIsTracking] = useState(false);
  const [progress, setProgress] = useState(0);
  const [eta, setETA] = useState(null);
  const [speed] = useState(65); // Average speed in km/h
  const [panelExpanded, setPanelExpanded] = useState(false);
  const intervalRef = useRef(null);

  // Get user location once
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setUserPos([pos.coords.latitude, pos.coords.longitude]),
        () => setUserPos([28.6139, 77.2090]) // fallback Delhi
      );
    }
  }, []);

  // Haversine formula for distance
  const calcDistance = (a, b) => {
    const toRad = (x) => (x * Math.PI) / 180;
    const R = 6371;
    const dLat = toRad(b[0] - a[0]);
    const dLon = toRad(b[1] - a[1]);
    const lat1 = toRad(a[0]);
    const lat2 = toRad(b[0]);
    const val =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1) * Math.cos(lat2) * Math.sin(dLon / 2) ** 2;
    return R * 2 * Math.atan2(Math.sqrt(val), Math.sqrt(1 - val));
  };

  const calculateETA = (distanceKm, speedKmh) => {
    const hours = distanceKm / speedKmh;
    const minutes = Math.round(hours * 60);
    if (minutes < 60) {
      return `${minutes} min`;
    } else {
      const hrs = Math.floor(minutes / 60);
      const mins = minutes % 60;
      return `${hrs}h ${mins}m`;
    }
  };

  const startTracking = () => {
    setStatus("üü¢ Live Tracking");
    setIsTracking(true);
    let index = busIndex;

    intervalRef.current = setInterval(() => {
      if (index < routeCoords.length - 1) {
        index++;
        const newPos = routeCoords[index];
        setBusPos(newPos);
        setBusIndex(index);

        // Calculate progress percentage
        const progressPercent = (index / (routeCoords.length - 1)) * 100;
        setProgress(progressPercent);

        if (userPos) {
          const dist = calcDistance(userPos, newPos);
          setDistance(dist.toFixed(2));
          setETA(calculateETA(dist, speed));
        }
      } else {
        clearInterval(intervalRef.current);
        setStatus("üèÅ Reached Destination");
        setIsTracking(false);
        setProgress(100);
      }
    }, 3000); // Faster updates for better UX
  };

  const stopTracking = () => {
    clearInterval(intervalRef.current);
    setStatus("‚è∏Ô∏è Tracking Paused");
    setIsTracking(false);
  };

  return (
    <div className="app">
      {/* Header */}
      <div className="header">
        <div className="header-left">
          <div className="bus-logo">üöå</div>
          <h1 className="header-title">SmartBus Live Tracker</h1>
        </div>
        <div className="header-status">
          <span className={`status-indicator ${
            status.includes('Live') ? 'status-running' : 
            status.includes('Paused') ? 'status-stopped' : 
            status.includes('Reached') ? 'status-reached' : 'status-stopped'
          }`}></span>
          <span>{status}</span>
        </div>
      </div>

      {/* Sidebar */}
      <div className="sidebar">
        <div className="route-info">
          <h3>üõ£Ô∏è Route Details</h3>
          <p><b>Origin:</b> Dehradun</p>
          <p><b>Destination:</b> Delhi</p>
          <p><b>Total Distance:</b> ~250 km</p>
        </div>

        {/* Progress Section */}
        <div className="progress-section">
          <div className="progress-header">
            <span><b>üìà Journey Progress</b></span>
            <span><b>{Math.round(progress)}%</b></span>
          </div>
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{width: `${progress}%`}}
            ></div>
          </div>
        </div>

        {/* Quick Stats */}
        <div className="quick-stats">
          <div className="stat-card">
            <div className="stat-value">{distance || "--"}</div>
            <div className="stat-label">Distance (km)</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">{speed}</div>
            <div className="stat-label">Speed (km/h)</div>
          </div>
        </div>

        {/* ETA Display */}
        {eta && (
          <div className="eta-display">
            üïí <b>ETA:</b> {eta}
          </div>
        )}

        {/* Control Buttons */}
        <div className="buttons">
          <button 
            onClick={startTracking} 
            disabled={isTracking}
            style={{opacity: isTracking ? 0.6 : 1}}
          >
            {isTracking ? 'üî¥ Tracking...' : 'üéØ Start Tracking'}
          </button>
          <button 
            onClick={stopTracking}
            disabled={!isTracking}
            style={{opacity: !isTracking ? 0.6 : 1}}
          >
            ‚èπÔ∏è Stop
          </button>
        </div>
      </div>

      {/* Map Container */}
      <div className="map-container">
        {/* Live Location Indicator */}
        <div className="live-location-indicator">
          <div className="pulse-dot"></div>
          Live Bus Location
        </div>

        <MapContainer
          center={routeCoords[0]}
          zoom={7}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer 
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          <Polyline 
            positions={routeCoords} 
            color="#00ffff" 
            weight={5}
            opacity={0.9}
          />

          {/* Bus Marker with Popup */}
          <Marker position={busPos} icon={busIcon}>
            <Popup className="bus-location-popup">
              <div style={{textAlign: 'center'}}>
                <strong>üöå Bus Current Location</strong><br/>
                <span>Progress: {Math.round(progress)}%</span><br/>
                <span>Status: {status}</span>
                {distance && <div>Distance from you: <b>{distance} km</b></div>}
              </div>
            </Popup>
          </Marker>

          {/* User Marker */}
          {userPos && (
            <Marker position={userPos} icon={userIcon}>
              <Popup>
                <div style={{textAlign: 'center'}}>
                  <strong>üìç Your Location</strong><br/>
                  <span>Lat: {userPos[0].toFixed(4)}</span><br/>
                  <span>Lng: {userPos[1].toFixed(4)}</span>
                </div>
              </Popup>
            </Marker>
          )}

          <MoveMapToBus busPosition={busPos} />
        </MapContainer>
      </div>
    </div>
  );
}

export default App;
