import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Polyline, Marker, Popup, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./App.css";

// Mobile.png Style Icons - Round Orange Bus  
const busIcon = L.divIcon({
  html: `
    <div class="mobile-bus-icon">
      <div class="bus-circle">
        <div class="bus-icon-mobile">üöå</div>
      </div>
    </div>
  `,
  iconSize: [44, 44],
  iconAnchor: [22, 22],
  popupAnchor: [0, -30],
  className: 'mobile-bus-marker'
});

// Simple Blue Dot for User
const userIcon = L.divIcon({
  html: `
    <div class="user-dot-marker">
      <div class="user-dot"></div>
    </div>
  `,
  iconSize: [20, 20],
  iconAnchor: [10, 10],
  popupAnchor: [0, -20],
  className: 'user-dot-marker-icon'
});

// Route: Dehradun ‚Üí Delhi
const routeCoords = [
  [30.3165, 78.0322], // Dehradun
  [29.8661, 77.8945], // Roorkee
  [29.4727, 77.7085], // Muzaffarnagar
  [29.2100, 77.0400], // Meerut
  [28.6139, 77.2090], // Delhi
];

function MoveMapToBus({ busPosition }) {
  const map = useMap();
  useEffect(() => {
    if (busPosition) map.panTo(busPosition);
  }, [busPosition, map]);
  return null;
}

function App() {
  const [busIndex, setBusIndex] = useState(0);
  const [busPos, setBusPos] = useState(routeCoords[0]);
  const [userPos, setUserPos] = useState(null);
  const [distance, setDistance] = useState(null);
  const [status, setStatus] = useState("Ready to Track");
  const [isTracking, setIsTracking] = useState(false);
  const [progress, setProgress] = useState(0);
  const [eta, setETA] = useState(null);
  const [speed] = useState(65); // Average speed in km/h
  const [panelExpanded, setPanelExpanded] = useState(false);
  const intervalRef = useRef(null);

  // Get user location once
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setUserPos([pos.coords.latitude, pos.coords.longitude]),
        () => setUserPos([28.6139, 77.2090]) // fallback Delhi
      );
    }
  }, []);

  // Haversine formula for distance
  const calcDistance = (a, b) => {
    const toRad = (x) => (x * Math.PI) / 180;
    const R = 6371;
    const dLat = toRad(b[0] - a[0]);
    const dLon = toRad(b[1] - a[1]);
    const lat1 = toRad(a[0]);
    const lat2 = toRad(b[0]);
    const val =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1) * Math.cos(lat2) * Math.sin(dLon / 2) ** 2;
    return R * 2 * Math.atan2(Math.sqrt(val), Math.sqrt(1 - val));
  };

  const calculateETA = (distanceKm, speedKmh) => {
    const hours = distanceKm / speedKmh;
    const minutes = Math.round(hours * 60);
    if (minutes < 60) {
      return `${minutes} min`;
    } else {
      const hrs = Math.floor(minutes / 60);
      const mins = minutes % 60;
      return `${hrs}h ${mins}m`;
    }
  };

  const startTracking = () => {
    setStatus("Live Tracking");
    setIsTracking(true);
    setPanelExpanded(true);
    let index = busIndex;

    intervalRef.current = setInterval(() => {
      if (index < routeCoords.length - 1) {
        index++;
        const newPos = routeCoords[index];
        setBusPos(newPos);
        setBusIndex(index);

        // Calculate progress percentage
        const progressPercent = (index / (routeCoords.length - 1)) * 100;
        setProgress(progressPercent);

        if (userPos) {
          const dist = calcDistance(userPos, newPos);
          setDistance(dist.toFixed(1));
          setETA(calculateETA(dist, speed));
        }
      } else {
        clearInterval(intervalRef.current);
        setStatus("Reached Destination");
        setIsTracking(false);
        setProgress(100);
      }
    }, 2000); // Faster updates for smooth mobile experience
  };

  const stopTracking = () => {
    clearInterval(intervalRef.current);
    setStatus("Tracking Paused");
    setIsTracking(false);
  };

  const togglePanel = () => {
    setPanelExpanded(!panelExpanded);
  };

  return (
    <div className="app">
      {/* Mobile Header */}
      <div className="mobile-header">
        <button className="back-button">
          ‚Üê
        </button>
        <h1 className="header-title-mobile">Live Tracking</h1>
      </div>

      {/* Map Container */}
      <div className="map-container">
        {/* Floating Controls */}
        <div className="floating-controls">
          <button 
            className={`control-button ${isTracking ? 'active' : ''}`}
            onClick={isTracking ? stopTracking : startTracking}
            title={isTracking ? 'Stop Tracking' : 'Start Tracking'}
