import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Polyline, Marker, Popup, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./App.css";

// Enhanced Custom Icons
const busIcon = L.icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/2967/2967350.png",
  iconSize: [45, 45],
  iconAnchor: [22, 22],
  popupAnchor: [0, -22],
  className: 'bus-marker-icon'
});

const userIcon = L.icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/64/64113.png",
  iconSize: [35, 35],
  iconAnchor: [17, 17],
  popupAnchor: [0, -17],
  className: 'user-marker-icon'
});

// Route: Dehradun ‚Üí Delhi
const routeCoords = [
  [30.3165, 78.0322], // Dehradun
  [29.8661, 77.8945], // Roorkee
  [29.4727, 77.7085], // Muzaffarnagar
  [29.2100, 77.0400], // Meerut
  [28.6139, 77.2090], // Delhi
];

function MoveMapToBus({ busPosition }) {
  const map = useMap();
  useEffect(() => {
    if (busPosition) map.panTo(busPosition);
  }, [busPosition, map]);
  return null;
}

function App() {
  const [busIndex, setBusIndex] = useState(0);
  const [busPos, setBusPos] = useState(routeCoords[0]);
  const [userPos, setUserPos] = useState(null);
  const [distance, setDistance] = useState(null);
  const [status, setStatus] = useState("üü° Ready to Track");
  const [isTracking, setIsTracking] = useState(false);
  const [progress, setProgress] = useState(0);
  const [eta, setETA] = useState(null);
  const [speed, setSpeed] = useState(65); // Average speed in km/h
  const intervalRef = useRef(null);

  // Get user location once
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setUserPos([pos.coords.latitude, pos.coords.longitude]),
        () => setUserPos([28.6139, 77.2090]) // fallback Delhi
      );
    }
  }, []);

  // Haversine formula for distance
  const calcDistance = (a, b) => {
    const toRad = (x) => (x * Math.PI) / 180;
    const R = 6371;
    const dLat = toRad(b[0] - a[0]);
    const dLon = toRad(b[1] - a[1]);
    const lat1 = toRad(a[0]);
    const lat2 = toRad(b[0]);
    const val =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1) * Math.cos(lat2) * Math.sin(dLon / 2) ** 2;
    return R * 2 * Math.atan2(Math.sqrt(val), Math.sqrt(1 - val));
  };

  const calculateETA = (distanceKm, speedKmh) => {
    const hours = distanceKm / speedKmh;
    const minutes = Math.round(hours * 60);
    if (minutes < 60) {
      return `${minutes} min`;
    } else {
      const hrs = Math.floor(minutes / 60);
      const mins = minutes % 60;
      return `${hrs}h ${mins}m`;
    }
  };

  const startTracking = () => {
    setStatus("üü¢ Live Tracking");
    setIsTracking(true);
    let index = busIndex;

    intervalRef.current = setInterval(() => {
      if (index < routeCoords.length - 1) {
        index++;
        const newPos = routeCoords[index];
        setBusPos(newPos);
        setBusIndex(index);

        // Calculate progress percentage
        const progressPercent = (index / (routeCoords.length - 1)) * 100;
        setProgress(progressPercent);

        if (userPos) {
          const dist = calcDistance(userPos, newPos);
          setDistance(dist.toFixed(2));
          setETA(calculateETA(dist, speed));
        }
      } else {
        clearInterval(intervalRef.current);
        setStatus("üèÅ Reached Destination");
        setIsTracking(false);
        setProgress(100);
      }
    }, 3000); // Faster updates for better UX
  };

  const stopTracking = () => {
    clearInterval(intervalRef.current);
    setStatus("‚è∏Ô∏è Tracking Paused");
    setIsTracking(false);
  };

  return (
    <div className="app">
      <div className="sidebar">
        <h2>
          <div className="bus-logo">üöå</div>
          SmartBus Live Tracker
        </h2>

        <div className="route-info">
          <h3>üìç Current Route</h3>
          <p><b>From:</b> Dehradun</p>
          <p><b>To:</b> Delhi</p>
        </div>

        <p>
          <b>Status:</b> 
          <span className={`status-indicator ${
            status.includes('Running') ? 'status-running' : 
            status.includes('Stopped') ? 'status-stopped' : 'status-reached'
          }`}></span>
          {status}
        </p>

        <p>
          <b>Distance to Bus:</b> 
          <span className="distance-badge">
            {distance ? `${distance} km` : "Calculating..."}
          </span>
        </p>

        <div className="buttons">
          <button onClick={startTracking}>üéØ Track Now</button>
          <button onClick={stopTracking}>‚èπÔ∏è Stop</button>
        </div>
      </div>

      <div className="map-container">
        <MapContainer
          center={routeCoords[0]}
          zoom={7}
          style={{ height: "100vh", width: "100%" }}
        >
          <TileLayer 
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          <Polyline 
            positions={routeCoords} 
            color="#00ffff" 
            weight={4}
            opacity={0.8}
          />
          <Marker position={busPos} icon={busIcon}></Marker>
          {userPos && <Marker position={userPos} icon={userIcon}></Marker>}
          <MoveMapToBus busPosition={busPos} />
        </MapContainer>
      </div>
    </div>
  );
}

export default App;
