import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap, Polyline } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./App.css";

// Mobile.png Style Bus Icon - Round Orange Circle
const busIcon = L.divIcon({
  html: `
    <div class="bus-marker-mobile">
      <div class="bus-icon-circle">
        <div class="bus-icon">üöå</div>
      </div>
    </div>
  `,
  iconSize: [40, 40],
  iconAnchor: [20, 20],
  popupAnchor: [0, -25],
  className: 'custom-bus-marker'
});

// User Location - Blue Dot
const userIcon = L.divIcon({
  html: `
    <div class="user-marker-mobile">
      <div class="user-dot"></div>
    </div>
  `,
  iconSize: [16, 16],
  iconAnchor: [8, 8],
  popupAnchor: [0, -15],
  className: 'custom-user-marker'
});

// Route coordinates
const routeCoords = [
  [30.3165, 78.0322], // Dehradun
  [29.8661, 77.8945], // Roorkee
  [29.4727, 77.7085], // Muzaffarnagar
  [29.2100, 77.0400], // Meerut
  [28.6139, 77.2090], // Delhi
];

// Auto-move map to bus location
function MoveMapToBus({ position }) {
  const map = useMap();
  useEffect(() => {
    if (position) map.panTo(position);
  }, [position, map]);
  return null;
}

function App() {
  const [busPosition, setBusPosition] = useState(routeCoords[0]);
  const [userPosition, setUserPosition] = useState(null);
  const [distance, setDistance] = useState("0.0");
  const [eta, setEta] = useState("-- min");
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTracking, setIsTracking] = useState(false);
  const [progress, setProgress] = useState(0);
  const intervalRef = useRef(null);

  // Get user location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setUserPosition([pos.coords.latitude, pos.coords.longitude]);
        },
        () => {
          // Fallback to Delhi if geolocation fails
          setUserPosition([28.6139, 77.2090]);
        }
      );
    } else {
      setUserPosition([28.6139, 77.2090]);
    }
  }, []);

  // Calculate distance using Haversine formula
  const calculateDistance = (pos1, pos2) => {
    const toRad = (x) => (x * Math.PI) / 180;
    const R = 6371; // Earth radius in km
    const dLat = toRad(pos2[0] - pos1[0]);
    const dLon = toRad(pos2[1] - pos1[1]);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRad(pos1[0])) * Math.cos(toRad(pos2[0])) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  // Update distance and ETA
  useEffect(() => {
    if (userPosition) {
      const dist = calculateDistance(userPosition, busPosition);
      setDistance(dist.toFixed(1));
      const estimatedTime = Math.round(dist / 0.8); // Assuming 0.8 km/min average speed
      setEta(estimatedTime + " min");
    }
  }, [busPosition, userPosition]);

  // Start bus tracking
  const startTracking = () => {
    setIsTracking(true);
    let index = currentIndex;

    intervalRef.current = setInterval(() => {
      if (index < routeCoords.length - 1) {
        index++;
        setBusPosition(routeCoords[index]);
        setCurrentIndex(index);
        const progressPercent = (index / (routeCoords.length - 1)) * 100;
        setProgress(progressPercent);
      } else {
        clearInterval(intervalRef.current);
        setIsTracking(false);
        setProgress(100);
      }
    }, 3000); // Move every 3 seconds
  };

  // Stop bus tracking
  const stopTracking = () => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      setIsTracking(false);
    }
  };

  // Auto-start tracking on component mount
  useEffect(() => {
    const autoStart = setTimeout(() => {
      if (!isTracking) {
        startTracking();
      }
    }, 2000);

    return () => {
      clearTimeout(autoStart);
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  return (
    <div className="app-mobile">
      {/* Mobile Header - Orange */}
      <div className="header-mobile">
        <button className="back-btn">‚Üê</button>
        <h1 className="header-title">Live Tracking</h1>
      </div>

      {/* Main Map Container */}
      <div className="map-container-mobile">
        <MapContainer
          center={busPosition}
          zoom={7}
          style={{ height: "100%", width: "100%" }}
          attributionControl={false}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Route Line */}
          <Polyline
            positions={routeCoords}
            color="#ff8c00"
            weight={4}
            opacity={0.7}
          />

          {/* Bus Marker - Round Orange */}
          <Marker position={busPosition} icon={busIcon}>
            <Popup>
              <div className="popup-content">
                <strong>üöå Live Bus Location</strong>
                <br />Progress: {Math.round(progress)}%
                <br />Status: {isTracking ? "Moving" : "Stopped"}
              </div>
            </Popup>
          </Marker>

          {/* User Marker - Blue Dot */}
          {userPosition && (
            <Marker position={userPosition} icon={userIcon}>
              <Popup>
                <div className="popup-content">
                  <strong>üìç Your Location</strong>
                </div>
              </Popup>
            </Marker>
          )}

          <MoveMapToBus position={busPosition} />
        </MapContainer>

        {/* Floating Control Buttons */}
        <div className="floating-controls-mobile">
          <button 
            className={`control-btn ${isTracking ? 'active' : ''}`}
            onClick={isTracking ? stopTracking : startTracking}
          >
            {isTracking ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
          </button>
        </div>

        {/* Bottom Distance Bar */}
        <div className="bottom-info-bar">
          <div className="info-item">
            <span className="info-label">Distance</span>
            <span className="info-value">{distance} km</span>
          </div>
          <div className="info-divider"></div>
          <div className="info-item">
            <span className="info-label">ETA</span>
            <span className="info-value">{eta}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
