import React, { useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./App.css";

// üöå Custom bus icon
const busIcon = new L.Icon({
  iconUrl: process.env.PUBLIC_URL + "/bus-icon.png", // place bus image in /public folder
  iconSize: [45, 45],
  iconAnchor: [22, 45],
  popupAnchor: [0, -45],
});

// üéØ Component to zoom map when tracking starts
function ZoomOnStart({ zoomLevel }) {
  const map = useMap();
  React.useEffect(() => {
    if (zoomLevel) {
      map.setZoom(zoomLevel, { animate: true });
    }
  }, [zoomLevel, map]);
  return null;
}

function App() {
  const [tracking, setTracking] = useState(false);
  const [zoomLevel, setZoomLevel] = useState(13);
  const [busPosition, setBusPosition] = useState([21.1702, 72.8311]); // Surat, example location

  const handleStartTracking = () => {
    setTracking(true);
    setZoomLevel(16); // zoom in when tracking starts
  };

  return (
    <div className="App">
      <header className="app-header">üü† Live Tracking</header>

      <MapContainer
        center={busPosition}
        zoom={zoomLevel}
        scrollWheelZoom={true}
        className="map"
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="¬© OpenStreetMap"
        />

        <Marker position={busPosition} icon={busIcon}>
          <Popup>üöå SmartBus is here!</Popup>
        </Marker>

        {/* Smooth zoom handler */}
        <ZoomOnStart zoomLevel={zoomLevel} />
      </MapContainer>

      <div className="bottom-panel">
        <button className="start-btn" onClick={handleStartTracking}>
          ‚ñ∂Ô∏è Start Tracking
        </button>
        <div className="eta-info">
          ‚è±Ô∏è 0.36 min ETA | üìç 0.09 km Away
        </div>
      </div>
    </div>
  );
}

export default App;
