import React, { useEffect, useState, useRef, useCallback } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap, Polyline } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./App.css";

// Dynamic bus icon based on tracking state
const getBusIcon = (isMoving) => L.divIcon({
  html: `
    <div class="bus-marker-mobile ${isMoving ? 'moving' : ''}">
      <div class="bus-icon-circle">
        <div class="bus-icon">üöå</div>
      </div>
    </div>
  `,
  iconSize: [40, 40],
  iconAnchor: [20, 20],
  popupAnchor: [0, -25],
  className: 'custom-bus-marker'
});

// User Location - Blue Dot
const userIcon = L.divIcon({
  html: `
    <div class="user-marker-mobile">
      <div class="user-dot"></div>
    </div>
  `,
  iconSize: [16, 16],
  iconAnchor: [8, 8],
  popupAnchor: [0, -15],
  className: 'custom-user-marker'
});

// Route coordinates
const routeCoords = [
  [30.3165, 78.0322], // Dehradun
  [29.8661, 77.8945], // Roorkee
  [29.4727, 77.7085], // Muzaffarnagar
  [29.2100, 77.0400], // Meerut
  [28.6139, 77.2090], // Delhi
];

// Auto-move map to bus location with zoom control
function MoveMapToBus({ position, zoom, shouldZoom }) {
  const map = useMap();
  useEffect(() => {
    if (position) {
      if (shouldZoom) {
        // Zoom to bus location when tracking starts
        map.setView(position, zoom || 10, {
          animate: true,
          duration: 1.5
        });
      } else {
        // Just pan to position during tracking
        map.panTo(position, {
          animate: true,
          duration: 0.8
        });
      }
    }
  }, [position, map, zoom, shouldZoom]);
  return null;
}

function App() {
  const [busPosition, setBusPosition] = useState(routeCoords[0]);
  const [userPosition, setUserPosition] = useState(null);
  const [distance, setDistance] = useState("0.0");
  const [eta, setEta] = useState("-- min");
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTracking, setIsTracking] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showExpandedInfo, setShowExpandedInfo] = useState(false);
  const [shouldZoomToStart, setShouldZoomToStart] = useState(false);
  const intervalRef = useRef(null);

  // Get user location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setUserPosition([pos.coords.latitude, pos.coords.longitude]);
        },
        () => {
          // Fallback to Delhi if geolocation fails
          setUserPosition([28.6139, 77.2090]);
        }
      );
    } else {
      setUserPosition([28.6139, 77.2090]);
    }
  }, []);

  // Calculate distance using Haversine formula
  const calculateDistance = (pos1, pos2) => {
    const toRad = (x) => (x * Math.PI) / 180;
    const R = 6371; // Earth radius in km
    const dLat = toRad(pos2[0] - pos1[0]);
    const dLon = toRad(pos2[1] - pos1[1]);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRad(pos1[0])) * Math.cos(toRad(pos2[0])) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  // Update distance and ETA
  useEffect(() => {
    if (userPosition) {
      const dist = calculateDistance(userPosition, busPosition);
      setDistance(dist.toFixed(1));
      const estimatedTime = Math.round(dist / 0.8); // Assuming 0.8 km/min average speed
      setEta(estimatedTime + " min");
      console.log('üìè Distance:', dist.toFixed(1), 'km, ETA:', estimatedTime, 'min');
    }
  }, [busPosition, userPosition]);

  // Start bus tracking with better logic
  const startTracking = useCallback(() => {
    console.log('üöå Starting bus tracking...');
    setIsTracking(true);
    setShouldZoomToStart(true); // Trigger zoom to bus location

    // Reset zoom trigger after a moment
    setTimeout(() => setShouldZoomToStart(false), 2000);

    intervalRef.current = setInterval(() => {
      setCurrentIndex(prevIndex => {
        const newIndex = prevIndex < routeCoords.length - 1 ? prevIndex + 1 : prevIndex;
        console.log(`üöå Moving from index ${prevIndex} to ${newIndex}`);

        if (newIndex < routeCoords.length) {
          const newPosition = routeCoords[newIndex];
          console.log(`üìç New position: ${newPosition[0]}, ${newPosition[1]}`);
          setBusPosition(newPosition);

          const progressPercent = (newIndex / (routeCoords.length - 1)) * 100;
          setProgress(progressPercent);
          console.log(`üìä Progress: ${Math.round(progressPercent)}%`);
        }

        if (newIndex >= routeCoords.length - 1) {
          console.log('üèÅ Bus reached destination!');
          clearInterval(intervalRef.current);
          setIsTracking(false);
          setProgress(100);
        }

        return newIndex;
      });
    }, 2500); // Faster updates - every 2.5 seconds
  }, []); // No dependencies needed

  // Stop bus tracking
  const stopTracking = () => {
    console.log('‚è∏Ô∏è Stopping bus tracking...');
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
    setIsTracking(false);
  };

  // Reset tracking to start
  const resetTracking = () => {
    console.log('üîÑ Resetting bus tracking...');
    stopTracking();
    setCurrentIndex(0);
    setBusPosition(routeCoords[0]);
    setProgress(0);
    setShowExpandedInfo(false); // Close expanded info on reset
  };

  // Toggle expanded info panel
  const toggleExpandedInfo = () => {
    setShowExpandedInfo(!showExpandedInfo);
  };

  // Auto-start tracking after component mount
  useEffect(() => {
    const autoStartTimer = setTimeout(() => {
      console.log('üöÄ Auto-starting tracking...');
      startTracking();
    }, 3000); // Wait 3 seconds then auto-start

    return () => {
      clearTimeout(autoStartTimer);
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [startTracking]); // Include startTracking as dependency

  // Debug useEffect to track state changes
  useEffect(() => {
    console.log(`üìä State Update - Index: ${currentIndex}, Tracking: ${isTracking}, Progress: ${Math.round(progress)}%`);
  }, [currentIndex, isTracking, progress]);


  return (
    <div className="app-mobile">
      {/* Mobile Header - Orange */}
      <div className="header-mobile">
        <button className="back-btn">‚Üê</button>
        <h1 className="header-title">Live Tracking</h1>
      </div>

      {/* Main Map Container */}
      <div className="map-container-mobile">
        <MapContainer
          center={busPosition}
          zoom={7}
          style={{ height: "100%", width: "100%" }}
          attributionControl={false}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Route Line */}
          <Polyline
            positions={routeCoords}
            color="#ff8c00"
            weight={4}
            opacity={0.7}
          />

          {/* Bus Marker - Round Orange with Animation */}
          <Marker position={busPosition} icon={getBusIcon(isTracking)}>
            <Popup>
              <div className="popup-content">
                <strong>üöå Live Bus Location</strong>
                <br />Progress: {Math.round(progress)}%
                <br />Status: {isTracking ? "Moving" : "Stopped"}
              </div>
            </Popup>
          </Marker>

          {/* User Marker - Blue Dot */}
          {userPosition && (
            <Marker position={userPosition} icon={userIcon}>
              <Popup>
                <div className="popup-content">
                  <strong>üìç Your Location</strong>
                </div>
              </Popup>
            </Marker>
          )}

          <MoveMapToBus 
            position={busPosition} 
            zoom={12}
            shouldZoom={shouldZoomToStart}
          />
        </MapContainer>


        {/* Floating Control Buttons */}
        <div className="floating-controls-mobile">
          <button 
            className={`control-btn info-btn ${showExpandedInfo ? 'expanded' : ''}`}
            onClick={toggleExpandedInfo}
            title="Show/Hide Live Info"
          >
            üìä
          </button>
          <button 
            className={`control-btn ${isTracking ? 'active' : ''}`}
            onClick={isTracking ? stopTracking : startTracking}
            title={isTracking ? 'Stop Tracking' : 'Start Tracking'}
          >
            {isTracking ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
          </button>
          <button 
            className="control-btn reset-btn"
            onClick={resetTracking}
            title="Reset to Start"
          >
            üîÑ
          </button>
        </div>



        {/* Expandable Info Circle - Left Side */}
        {showExpandedInfo && (
          <div className="expanded-info-circle">
            <button 
              className="close-info-btn"
              onClick={toggleExpandedInfo}
              title="Close Info"
            >
              ‚úï
            </button>

            <div className="info-circle-content">
              {/* Main Status */}
              <div className="info-main">
                <div className="info-main-icon">
                  {isTracking ? 'üü¢' : (progress === 100 ? 'üèÅ' : 'üü°')}
                </div>
                <div className="info-main-text">
                  {isTracking ? 'Tracking' : (progress === 100 ? 'Complete' : 'Ready')}
                </div>
              </div>

              {/* Progress Ring */}
              <div className="progress-ring-container">
                <svg className="progress-ring-svg" width="80" height="80">
                  <defs>
                    <linearGradient id="progressGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                      <stop offset="0%" stopColor="#fff" stopOpacity="0.9" />
                      <stop offset="50%" stopColor="#f0f8ff" stopOpacity="1" />
                      <stop offset="100%" stopColor="#e6f3ff" stopOpacity="0.8" />
                    </linearGradient>
                    <filter id="glow">
                      <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
                      <feMerge> 
                        <feMergeNode in="coloredBlur"/>
                        <feMergeNode in="SourceGraphic"/> 
                      </feMerge>
                    </filter>
                  </defs>
                  <circle
                    cx="40"
                    cy="40"
                    r="35"
                    stroke="rgba(255,255,255,0.3)"
                    strokeWidth="3"
                    fill="none"
                  />
                  <circle
                    cx="40"
                    cy="40"
                    r="35"
                    stroke="url(#progressGradient)"
                    strokeWidth="4"
                    fill="none"
                    strokeLinecap="round"
                    strokeDasharray={`${2 * Math.PI * 35}`}
                    strokeDashoffset={`${2 * Math.PI * 35 * (1 - progress / 100)}`}
                    style={{ transition: 'stroke-dashoffset 0.6s cubic-bezier(0.4, 0, 0.2, 1)' }}
                    filter="url(#glow)"
                  />
                </svg>
                <div className="progress-text">
                  <span className="progress-percent">{Math.round(progress)}%</span>
                </div>
              </div>

              {/* Info Items */}
              <div className="info-items">
                <div className="info-item-expanded">
                  <span className="info-icon-expanded">üìç</span>
                  <span className="info-value-expanded">
                    {distance || '0.0'}
                  </span>
                  <span className="info-unit-expanded">KM</span>
                </div>
                <div className="info-item-expanded">
                  <span className="info-icon-expanded">üïí</span>
                  <span className="info-value-expanded">
                    {eta ? eta.split(' ')[0] : '0'}
                  </span>
                  <span className="info-unit-expanded">MIN</span>
                </div>
              </div>
            </div>
          </div>
        )}


      </div>
    </div>
  );
}

export default App;
