import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Polyline, Marker, Popup, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./App.css";

/* -------------------------
   Custom DIV ICONS (styled via CSS)
   ------------------------- */
const busIcon = L.divIcon({
  html: `
    <div class="mobile-bus-icon">
      <div class="bus-circle">
        <div class="bus-icon-mobile"></div>
      </div>
    </div>
  `,
  iconSize: [48, 48],
  iconAnchor: [24, 24],
  popupAnchor: [0, -36],
  className: "mobile-bus-marker"
});

const userIcon = L.divIcon({
  html: `
    <div class="user-dot-marker">
      <div class="user-dot"></div>
    </div>
  `,
  iconSize: [20, 20],
  iconAnchor: [10, 10],
  popupAnchor: [0, -12],
  className: "user-dot-marker-icon"
});

/* -------------------------
   Route Coordinates (Dehradun -> Delhi)
   ------------------------- */
const routeCoords = [
  [30.3165, 78.0322], // Dehradun
  [29.8661, 77.8945], // Roorkee
  [29.4727, 77.7085], // Muzaffarnagar
  [29.2100, 77.0400], // Meerut
  [28.6139, 77.2090], // Delhi
];

/* -------------------------
   Helper: Pan map to bus (custom hook component)
   ------------------------- */
function MoveMapToBus({ busPosition, enablePan = true }) {
  const map = useMap();
  useEffect(() => {
    if (busPosition && enablePan) {
      map.panTo(busPosition, { animate: true, duration: 0.8 });
    }
  }, [busPosition, map, enablePan]);
  return null;
}

/* -------------------------
   Main Component
   ------------------------- */
export default function App() {
  // states
  const [busPos, setBusPos] = useState(routeCoords[0]);
  const [userPos, setUserPos] = useState(null);
  const [distance, setDistance] = useState(null);
  const [status, setStatus] = useState("Ready to Track");
  const [isTracking, setIsTracking] = useState(false);
  const [progress, setProgress] = useState(0);
  const [eta, setETA] = useState("--");
  const [speed] = useState(65); // km/h average
  const [panelExpanded, setPanelExpanded] = useState(true);

  // refs to keep interval and index
  const intervalRef = useRef(null);
  const indexRef = useRef(0);

  // get user location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setUserPos([pos.coords.latitude, pos.coords.longitude]),
        () => setUserPos([28.6139, 77.2090]) // fallback Delhi
      );
      // also watch position to update user marker live (optional)
      const watchId = navigator.geolocation.watchPosition(
        (pos) => setUserPos([pos.coords.latitude, pos.coords.longitude]),
        () => {},
        { enableHighAccuracy: true, maximumAge: 5000 }
      );
      return () => navigator.geolocation.clearWatch(watchId);
    } else {
      setUserPos([28.6139, 77.2090]); // fallback
    }
  }, []);

  // Haversine distance (km)
  const calcDistance = (a, b) => {
    if (!a || !b) return null;
    const toRad = (x) => (x * Math.PI) / 180;
    const R = 6371;
    const dLat = toRad(b[0] - a[0]);
    const dLon = toRad(b[1] - a[1]);
    const lat1 = toRad(a[0]);
    const lat2 = toRad(b[0]);
    const val =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1) * Math.cos(lat2) * Math.sin(dLon / 2) ** 2;
    return R * 2 * Math.atan2(Math.sqrt(val), Math.sqrt(1 - val));
  };

  const calculateETA = (distanceKm, speedKmh) => {
    if (!distanceKm || !speedKmh) return "--";
    const minutes = Math.round((distanceKm / speedKmh) * 60);
    if (minutes < 60) return `${minutes} min`;
    const hrs = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hrs}h ${mins}m`;
  };

  // update derived values whenever bus or user move
  useEffect(() => {
    if (userPos && busPos) {
      const d = calcDistance(userPos, busPos);
      if (d !== null) {
        setDistance(d.toFixed(2));
        setETA(calculateETA(d, speed));
      }
    }
  }, [busPos, userPos, speed]);

  // safe start tracking
  const startTracking = () => {
    if (isTracking) return;
    setStatus("Live Tracking");
    setIsTracking(true);
    setPanelExpanded(true);

    // start from current indexRef (helpful when restarting)
    let i = indexRef.current;
    if (i >= routeCoords.length - 1) {
      // if already finished, reset
      i = 0;
      indexRef.current = 0;
      setProgress(0);
      setBusPos(routeCoords[0]);
    }

    intervalRef.current = setInterval(() => {
      // move forward by one step
      if (i < routeCoords.length - 1) {
        i++;
        indexRef.current = i;
        const next = routeCoords[i];
        setBusPos(next);

        // progress percentage
        const pct = (i / (routeCoords.length - 1)) * 100;
        setProgress(pct);

        // update distance/eta done by effect above (busPos changed)
      } else {
        // reached destination
        clearInterval(intervalRef.current);
        intervalRef.current = null;
        setStatus("Reached Destination");
        setIsTracking(false);
        setProgress(100);
      }
    }, 2000); // 2s for smoother mobile feel
  };

  const stopTracking = () => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
    setIsTracking(false);
    setStatus("Tracking Paused");
  };

  const togglePanel = () => setPanelExpanded((p) => !p);

  // clear interval on unmount
  useEffect(() => {
    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, []);

  return (
    <div className="app-root">
      {/* Mobile Header */}
      <header className="mobile-header">
        <button className="back-button" onClick={() => { /* optional back behavior */ }}>
          ←
        </button>
        <div className="header-title-mobile">Live Tracking</div>
        <div className="header-right">
          <button className={`mini-btn ${isTracking ? "active" : ""}`} onClick={isTracking ? stopTracking : startTracking}>
            {isTracking ? "⏹️" : "▶️"}
          </button>
        </div>
      </header>

      {/* Map and Floating UI */}
      <div className="map-wrapper">
        <div className="floating-controls">
          <button className={`control-button ${isTracking ? "active" : ""}`} onClick={isTracking ? stopTracking : startTracking}>
            {isTracking ? "Stop" : "Start"}
          </button>
          <button className="control-button" onClick={togglePanel}>Details</button>
        </div>

        <MapContainer center={routeCoords[0]} zoom={9} className="map-container" attributionControl={false}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Polyline positions={routeCoords} color="#ff8c00" weight={5} opacity={0.9} />
          <Marker position={busPos} icon={busIcon}>
            <Popup>
              <div style={{ textAlign: "center", minWidth: 160 }}>
                <strong>🚌 Live Bus</strong>
                <div style={{ marginTop: 6 }}>Status: <strong style={{ color: "#ff8c00" }}>{status}</strong></div>
                <div style={{ marginTop: 8 }}>Progress: <strong>{Math.round(progress)}%</strong></div>
                {distance && <div style={{ marginTop: 6 }}>📍 {distance} km away</div>}
              </div>
            </Popup>
          </Marker>
          {userPos && (
            <Marker position={userPos} icon={userIcon}>
              <Popup>
                <div style={{ textAlign: "center" }}>
                  <strong>📍 Your Location</strong>
                  <div style={{ fontSize: 12, color: "#666", marginTop: 4 }}>
                    {userPos[0].toFixed(4)}, {userPos[1].toFixed(4)}
                  </div>
                </div>
              </Popup>
            </Marker>
          )}

          <MoveMapToBus busPosition={busPos} enablePan={isTracking} />
        </MapContainer>

        {/* Bottom Panel */}
        <aside className={`bottom-panel ${panelExpanded ? "expanded" : "collapsed"}`}>
          <div className="panel-handle" onClick={togglePanel}>
            <div className="panel-drag-indicator" />
          </div>

          <div className="bus-info-card">
            <div className="bus-info-header">
              <div className="bus-emoji">🚌</div>
              <div className="bus-meta">
                <div className="bus-info-title">Dehradun → Delhi Express</div>
                <div className="bus-sub">{status}</div>
              </div>
            </div>

            <div className="progress-row">
              <div className="progress-left">
                <div className="progress-label">Journey Progress</div>
                <div className="progress-value">{Math.round(progress)}%</div>
              </div>
              <div className="progress-bar">
                <div className="progress-fill" style={{ width: `${progress}%` }} />
              </div>
            </div>

            <div className="stats-grid">
              <div className="stat">
                <div className="stat-number">{distance ?? "--"}</div>
                <div className="stat-label">Distance (km)</div>
              </div>
              <div className="stat">
                <div className="stat-number">{speed}</div>
                <div className="stat-label">Speed (km/h)</div>
              </div>
              <div className="stat">
                <div className="stat-number">{eta ?? "--"}</div>
                <div className="stat-label">ETA</div>
              </div>
              <div className="stat">
                <div className="stat-number">250</div>
                <div className="stat-label">Total (km)</div>
              </div>
            </div>

            <div className="panel-actions">
              <button className="btn-primary" onClick={isTracking ? stopTracking : startTracking}>
                {isTracking ? "⏹️ Stop Tracking" : "🎯 Start Tracking"}
              </button>
              <button className="btn-secondary" onClick={() => alert("More details coming soon!")}>
                📋 Details
              </button>
            </div>
          </div>
        </aside>
      </div>
    </div>
  );
}
